/*eslint-disable*/
function solve(n) {
  if (n > 10e6) {
    for (var a = 0, b = 0, result = 0, i = Math.abs(n / 2); i <= n; i++) {
      var a1 = 0,
        b1 = 0;
      (a = i), (b = n - i);
      for (var j = 0; j < a.toString().length; j++)
        a1 += Number(a.toString().charAt(j));
      for (var j = 0; j < b.toString().length; j++)
        b1 += Number(b.toString().charAt(j));
      result < a1 + b1 && (result = a1 + b1);
    }
    return result;
  } else {
    for (var a = 0, b = 0, result = 0, i = 1; i <= n; i++) {
      var a1 = 0,
        b1 = 0;
      (a = i), (b = n - i);
      for (var j = 0; j < a.toString().length; j++)
        a1 += Number(a.toString().charAt(j));
      for (var j = 0; j < b.toString().length; j++)
        b1 += Number(b.toString().charAt(j));
      result < a1 + b1 && (result = a1 + b1);
    }
    return result;
  }
}
console.log(solve(10e7));
